import React, { useState, Component } from 'react';

import { Col, Row, Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import { Mutation, renderToStringWithData } from 'react-apollo';
import { useMutation } from "@apollo/react-hooks";
import gql from "graphql-tag";
import 'bootstrap/dist/css/bootstrap.min.css';
import "./SignUp.css";
import {
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
} from 'reactstrap';
import { Form as FinalForm, Field } from 'react-final-form';
import client from './index';


const CREATE_USER = gql`
  mutation CreateUser($input: UserInput!) {
    createUser(input: $input) {
      id
      username
    }
  }
`;

const GET_USER = gql`
          {
            User{ 
              username
              firstName
            }
          }
        `;


const SignUp = ({ post, onClose }) => (
  <FinalForm
    onSubmit={async({
      id,
      username,
      location,
      phone,
      email,
      password }) => {
      const input = {
        id,
        username,
        location,
        phone,
        email,
        password };

      await client.mutate({
        variables: { input },
        mutation: CREATE_USER,
        refetchQueries: () => [{ query: GET_USER }],
      });
      onClose();
    }}
    initialValues={post}
    render={({ handleSubmit, pristine, invalid }) => (
      <Modal isOpen toggle={onClose}>
        <Form onSubmit={alert("fuck")}>
          <ModalHeader toggle={onClose}>
          New User
          </ModalHeader>
          <ModalBody>
            <FormGroup>
              <Label>Username</Label>
              <Field
                required
                name="username"
                className="form-control"
                component="input"
              />
            </FormGroup>
            <FormGroup>
              <Label>Location</Label>
              <Field
                required
                name="location"
                className="form-control"
                component="input"
              />
            </FormGroup>
            <FormGroup>
              <Label>Phone</Label>
              <Field
                required
                name="phone"
                className="form-control"
                component="input"
              />
            </FormGroup>
            <FormGroup>
              <Label>Email</Label>
              <Field
                required
                name="email"
                className="form-control"
                component="input"
              />
            </FormGroup>
            <FormGroup>
              <Label>Password</Label>
              <Field
                required
                name="password"
                className="form-control"
                component="input"
              />
            </FormGroup>
          </ModalBody>
          <ModalFooter>
            <Button type="submit" disabled={pristine} color="primary">Save</Button>
            <Button color="secondary" onClick={onClose}>Cancel</Button>
          </ModalFooter>
        </Form>
      </Modal>
    )}
  />
);

export default SignUp;




