import React, { Component } from "react";
import "./App.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import gql from "graphql-tag";
import { Query } from "react-apollo";
import { Link, BrowserRouter as Router, Route } from 'react-router-dom';


class Users extends Component {

  render() {
    return (
      <Query
        query={gql`
          {
            User{ 
              name
            }
          }
        `}
      >
        {({ loading, error, data }) => {
          if (loading) return <p>Loading...</p>;
          if (error) return <p>Error</p>;

          return (
            <div>
              <h3>User's Page</h3>
              <ul className="List">
                {data.User.map(user => {
                  return (
                    <li><Link to={`/user/${user.name}`}>{user.name}'s Reviews</Link></li>
                  );
                })}
              </ul>
            </div>
          );
        }
        }
      </Query>);
  }
}

class ProfilePage extends Component {
  constructor(props) {
    super(props);
    this.state = { name: this.props.name };
  }

  render() {
    return (
      <Query
        query={gql`
              query User($name: String!){
                User(name: $name, first:1){ 
                  reviews_received{
                    stars
                    text
                    reviewWriter{
                      name
                    }
                  }
                  name
                  sex
                }
              }
          `}
        variables={{ name: this.state.name }}
      >
        {({ loading, error, data }) => {
          if (loading) return <p>Loading...</p>;
          if (error) return <p>Error</p>;
          let userInfo = data.User[0];
          return (
            <div>
              <h3>Name: {userInfo.name}</h3>
              <h4>Gender: {userInfo.sex}</h4>

              <ul className="list">
                {userInfo.reviews_received.map(review => {
                  return (
                    <ul className="List">
                      <li><strong>Reviewed By: {review.reviewWriter.name}</strong></li>
                      <li>Rating: {review.stars}/5</li>
                      <li>Comment: {review.text}</li>
                    </ul>
                  );
                })}
              </ul>
            </div>
          );
        }
        }
      </Query>
    );
  }
}

const IndexPage = () => {
  return (
    <h3>Home Page</h3>
  );
};
const AboutPage = () => {
  return (
    <h3>About Page</h3>
  );
};

const UsersPage = () => {
  return (
    <>
      {
        <Users />
      }
    </>
  );
};
const UserPage = ({ match, location }) => {
  const { params: { name } } = match;

  return (
    <>
      <ProfilePage name={name} />
    </>
  );
};

class App extends Component {
  render() {
    return (
      <div>
        <Navbar bg="light" expand="lg">
          <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mr-auto">
              <Nav.Link to="/">Home</Nav.Link>
              <Nav.Link to="/users">Users</Nav.Link>
              <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                <NavDropdown.Divider />
                <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
              </NavDropdown>
            </Nav>
            <Form inline>
              <FormControl type="text" placeholder="Search" className="mr-sm-2" />
              <Button variant="outline-success">Search</Button>
            </Form>
          </Navbar.Collapse>
        </Navbar>
        <Router>
          <Route exact path="/" component={IndexPage} />
          <Route exact path="/users" component={UsersPage} />
          <Route exact path="/user/:name" component={UserPage} />
        </Router>

      </div>
    );
  }
}

export default App;
