import React, { useState, Component } from 'react';

import { Col, Row, Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import { Mutation, renderToStringWithData } from 'react-apollo';
import { useMutation } from "@apollo/react-hooks";
import gql from "graphql-tag";
import 'bootstrap/dist/css/bootstrap.min.css';
import "./SignUp.css";
import {
    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter,
} from 'reactstrap';
import { Form as FinalForm, Field } from 'react-final-form';
import client from './index';

function getId() {
    var result = '';
    var characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    var charactersLength = characters.length;
    for (var i = 0; i < 8; i++) {
        result += characters.charAt(Math.floor(Math.random() * charactersLength));
    }
    return result;
}


const CREATE_REVIEW = gql`
  mutation CreateReview
    (
        $id: ID!, 
        $title: String!, 
        $stars: Int!, 
        $text: String,
        $username1: String!,
        $username2: String!
        ) 
            {
        CreateReview
                    (
                        id: $id, 
                        title: $title, 
                        stars: $stars, 
                        text: $text, 
                        ) {
        id
        }
        AddUserReviews_written
                    (
                        from: {
                            username: $username1
                        }, to: {
                            username: $username2
                        }) {
            from {
                username
            }
        }
        AddUserReviews_received
                    (
                        from: {
                            username: $username2
                        }, to: {
                            username: $username1
                        }) {
            from {
                username
            }
        }
  }

`;

const GET_USER = gql`
          {
            User{ 
              username
              firstName
            }
          }
        `;


const WriteReview = ({ post, onClose }) => (
    <FinalForm
        onSubmit={async ({
            username_from,
            password,
            title,
            stars,
            text,
            username_to }) => {
            const input = {
                username_from,
                password,
                title,
                stars,
                text,
                username_to
            };

            await client.mutate({
                mutation: CREATE_REVIEW,
                variables: { id: getId(), title: input.title, stars: input.stars, text: input.text, username1: input.username_from, username2: input.username_to},
                refetchQueries: () => [{ query: GET_USER }],
            });
            onClose();
        }}
        initialValues={post}
        render={({ handleSubmit, pristine, invalid }) => (
            <Modal isOpen toggle={onClose}>
                <Form onSubmit={handleSubmit}>
                    <ModalHeader toggle={onClose}>
                        New Review
          </ModalHeader>
                    <ModalBody>
                        <FormGroup>
                            <Label>Your Username</Label>
                            <Field
                                required
                                name="username_from"
                                className="form-control"
                                component="input"
                            />
                        </FormGroup>
                        <FormGroup>
                            <Label>Your Password</Label>
                            <Field
                                required
                                name="password"
                                className="form-control"
                                component="input"
                            />
                        </FormGroup>
                        <FormGroup>
                            <Label>Who are you reviewing?</Label>
                            <Field
                                required
                                name="username_to"
                                className="form-control"
                                component="input"
                            />
                        </FormGroup>
                        <FormGroup>
                            <Label>Title</Label>
                            <Field
                                required
                                name="title"
                                className="form-control"
                                component="input"
                            />
                        </FormGroup>
                        <FormGroup>
                            <Label>Stars</Label>
                            <Field
                                required
                                name="stars"
                                className="form-control"
                                component="input"
                            />
                        </FormGroup>
                        <FormGroup>
                            <Label>Text</Label>
                            <Field
                                required
                                name="text"
                                className="form-control"
                                component="input"
                            />
                        </FormGroup>
                    </ModalBody>
                    <ModalFooter>
                        <Button type="submit" disabled={pristine} color="primary">Write Review</Button>
                        <Button color="secondary" onClick={onClose}>Cancel</Button>
                    </ModalFooter>
                </Form>
            </Modal>
        )}
    />
);

export default WriteReview;
